Con lista circular:<br /><br /><pre class="prettyprint">#include &lt;iostream&gt;<br />#include &lt;cstdlib&gt;<br /><br />// Declaración de la estructura NODO<br />struct NODO{<br />  int dato;<br />  NODO* siguiente;<br />};<br /><br />struct LISTA_CIRCULAR{<br />  NODO* actual;<br />  NODO* final;<br />};<br /><br />void inicializar&#40;LISTA_CIRCULAR* lista&#41;;<br /><br />// Inserta en la parte de atras de la lista<br />void meter&#40;int dato, LISTA_CIRCULAR* lista&#41;;<br />// Comprueba si un elemento ya esta almacenado en la lista<br />bool esta&#40;int dato, LISTA_CIRCULAR* lista&#41;;<br /><br />void mostrar_lista&#40;LISTA_CIRCULAR* lista&#41;;<br /><br />void siguiente_turno&#40;LISTA_CIRCULAR* lista&#41;;<br /><br /><br /><br />// Funciones usadas en el programa<br />int main&#40;&#41;{<br /><br />  LISTA_CIRCULAR lista;<br />  inicializar&#40;&amp;lista&#41;;<br /><br />  for &#40;int i = 1; i &lt; 5; ++i&#41;<br />  {<br />    meter&#40;i, &amp;lista&#41;;<br />  }<br />  // Cola: 1, 2, 3, 4<br />  mostrar_lista&#40;&amp;lista&#41;;<br /><br />  for &#40;int i = 0; i &lt; 7; ++i&#41;<br />  {<br />    siguiente_turno&#40;&amp;lista&#41;;<br />    mostrar_lista&#40;&amp;lista&#41;;<br />  }<br /><br />  system&#40;&quot;pause&quot;&#41;;<br />  return 0;<br /><br />}<br /><br />void inicializar&#40;LISTA_CIRCULAR* lista&#41;<br />{<br />  lista-&gt;actual = NULL;<br />  lista-&gt;final = NULL;<br />}<br /><br />void meter&#40;int elemento, LISTA_CIRCULAR* lista&#41;<br />{<br />  NODO* nuevo = new NODO&#40;&#41;;<br />  nuevo-&gt;dato = elemento;<br />  nuevo-&gt;siguiente = NULL;<br /><br />  if &#40; lista-&gt;actual == NULL &#41;<br />  {<br />    // La lista esta vacia<br />    lista-&gt;actual = nuevo;<br />    lista-&gt;final = nuevo;<br />  }<br />  else<br />  {<br />    lista-&gt;final-&gt;siguiente = nuevo;<br />    lista-&gt;final = nuevo;<br />  }<br />  nuevo-&gt;siguiente = lista-&gt;actual;<br />}<br /><br />bool esta&#40;int dato, LISTA_CIRCULAR* lista&#41;<br />{<br />  NODO* aux = lista-&gt;actual;<br />  bool centinela = false;<br /><br />  if &#40;aux != NULL&#41;<br />  {<br />    while &#40; dato != aux-&gt;dato &amp;&amp; !centinela &#41;<br />    {<br />      if &#40;aux = lista-&gt;actual&#41;{<br />        // Hemos dado la vuelta completa<br />        centinela = true;<br />      }<br />      aux = aux-&gt;siguiente;<br />    }<br /><br />    return dato == aux-&gt;dato;<br />  }<br />  return false;<br />}<br /><br />void mostrar_lista&#40;LISTA_CIRCULAR* lista&#41;<br />{<br />  NODO* aux = lista-&gt;actual;<br /><br />  if &#40;aux != NULL&#41;<br />  {<br />    do<br />    {<br />      std::cout &lt;&lt; aux-&gt;dato &lt;&lt; &quot;, &quot;;<br />      aux = aux-&gt;siguiente;<br />      <br />    } while &#40;aux != lista-&gt;actual&#41;;<br />    std::cout &lt;&lt; &quot;\n&quot;;<br />  }<br />}<br /><br />void siguiente_turno&#40;LISTA_CIRCULAR* lista&#41;<br />{<br />  if &#40;lista-&gt;actual == NULL&#41;<br />  {<br />    std::cout &lt;&lt; &quot;Error: No hay elementos en la lista\n&quot;;<br />  }<br />  else<br />  {<br />    int turno_actual = lista-&gt;actual-&gt;dato;<br />    std::cout &lt;&lt; &quot;Turno actual: &quot; &lt;&lt; turno_actual &lt;&lt; &quot;\n&quot;;<br />    lista-&gt;actual = lista-&gt;actual-&gt;siguiente;<br />  }<br />}</pre><p>Estadísticas: Publicado por <a href="http://losersjuegos.com.ar/foro/memberlist.php?mode=viewprofile&amp;u=2057">Juanxo</a> — Jue May 12, 2011 9:21 pm</p><hr />
]]></content>
