Esto me compila, y funciona correctamente:<br /><br /><pre class="prettyprint">#include &lt;iostream&gt;<br />#include &lt;cstdlib&gt;<br /><br />// Declaración de la estructura NODO<br />struct NODO{<br />   int dato;<br />   NODO *siguiente;<br />};<br /><br />struct COLA{<br />    NODO* cabeza;<br />    NODO* final;<br />};<br /><br />void inicializar&#40;COLA* cola&#41;;<br /><br />// Inserta en la parte de atras de la cola<br />void meter&#40;int dato, COLA* cola&#41;;<br />// Elimina del principio de la cola<br />int sacar&#40;COLA cola&#41;;<br />// Comprueba si un elemento ya esta almacenado en la cola<br />bool esta&#40;int dato, COLA* cola&#41;;<br /><br />void mostrar_cola&#40;COLA* cola&#41;;<br /><br />void siguiente_turno&#40;COLA* cola&#41;;<br /><br /><br /><br />// Funciones usadas en el programa<br />int main&#40;&#41;{<br />    <br />    COLA cola;<br />    inicializar&#40;&amp;cola&#41;;<br />    <br />    for &#40;int i = 1; i &lt; 5; ++i&#41;<br />    {<br />        meter&#40;i, &amp;cola&#41;;<br />    }<br />    // Cola: 1, 2, 3, 4<br />    mostrar_cola&#40;&amp;cola&#41;;<br />    <br />    for &#40;int i = 0; i &lt; 15; ++i&#41;<br />    {<br />        siguiente_turno&#40;&amp;cola&#41;;<br />        mostrar_cola&#40;&amp;cola&#41;;<br />    }<br />    <br />    system&#40;&quot;pause&quot;&#41;;<br />    return 0;<br />    <br />}<br /><br />void inicializar&#40;COLA* cola&#41;<br />{<br />    cola-&gt;cabeza = NULL;<br />    cola-&gt;final = NULL;<br />}<br /><br />void meter&#40;int elemento, COLA* cola&#41;<br />{<br />    NODO* nuevo = new NODO&#40;&#41;;<br />    nuevo-&gt;dato = elemento;<br />    nuevo-&gt;siguiente = NULL;<br />    <br />    if &#40; cola-&gt;cabeza == NULL &#41;<br />    {<br />        // La cola esta vacia<br />        cola-&gt;cabeza = nuevo;<br />        cola-&gt;final = nuevo;<br />    }<br />    else<br />    {<br />        cola-&gt;final-&gt;siguiente = nuevo;<br />        cola-&gt;final = nuevo;<br />    }<br />}<br /><br />int sacar&#40;COLA* cola&#41;<br />{<br />    if &#40; cola-&gt;cabeza == NULL&#41;<br />    {<br />        // esta vacia<br />        return -1;<br />    }<br />    else<br />    {<br />        // Quitamos la cabeza y devolvemos su valor<br />        NODO* sacado = cola-&gt;cabeza;<br />        cola-&gt;cabeza = cola-&gt;cabeza-&gt;siguiente;<br />        int dato = sacado-&gt;dato;<br />        delete sacado;<br />        return dato;<br />    }<br />}<br /><br />bool esta&#40;int dato, COLA* cola&#41;<br />{<br />    NODO* aux = cola-&gt;cabeza;<br />    <br />    while &#40;aux != NULL &amp;&amp; dato != aux-&gt;dato&#41;<br />    {<br />        aux = aux-&gt;siguiente;<br />    }<br />    <br />    return aux != NULL;<br />}<br /><br />void mostrar_cola&#40;COLA* cola&#41;<br />{<br />    NODO* aux = cola-&gt;cabeza;<br />    <br />    while &#40;aux != NULL&#41;<br />    {<br />        std::cout &lt;&lt; aux-&gt;dato &lt;&lt; &quot;, &quot;;<br />        aux = aux-&gt;siguiente;<br />        std::cout &lt;&lt; &quot;\n&quot;;<br />    }<br />}<br /><br />void siguiente_turno&#40;COLA* cola&#41;<br />{<br />    if &#40;cola-&gt;cabeza == NULL&#41;<br />    {<br />        std::cout &lt;&lt; &quot;Error: No hay elementos en la cola\n&quot;;<br />    }<br />    else<br />    {<br />        int turno_actual = sacar&#40;cola&#41;;<br />        std::cout &lt;&lt; &quot;Turno actual: &quot; &lt;&lt; turno_actual &lt;&lt; &quot;\n&quot;;<br />        meter&#40;turno_actual, cola&#41;;<br />    }<br />}</pre><p>Estadísticas: Publicado por <a href="http://losersjuegos.com.ar/foro/memberlist.php?mode=viewprofile&amp;u=2057">Juanxo</a> — Jue May 12, 2011 9:04 pm</p><hr />
