 <br />    <br />    if tecla_pres&#91;K_LEFT&#93;:<br />        bol.left = bol.left - 5<br />        bola_tl = bola_tl - 5<br />        <br />        if bola_tl &lt;10&gt; 10:<br />               <br />                bola_tl = 10<br />                campo_x = campo_x + ayu<br />            else:<br />                bola_tl = 10<br />                bol.left = 10<br />        <br />    if tecla_pres&#91;K_RIGHT&#93;:<br />        bol.left = bol.left + 5<br />        bola_tl = bola_tl + 5<br />        <br />        <br />        if bola_tl &gt; 290:<br />            if bol.left  &lt;4970&gt; 20:<br />                   <br />                   bola_tl = 290<br />                   campo_x = campo_x - ayu<br />            else:      <br />              bola_tl = 290<br />              bol.left = 4970<br />             <br />               <br />              <br />               <br />    if tecla_pres&#91;K_UP&#93;:<br />        bol.top = bol.top - 5<br />        bola_tt = bola_tt - 5<br />        <br />        if bola_tt &lt;10&gt; 10:<br />                <br />                bola_tt = 10<br />                campo_y = campo_y + ayu<br />            else:<br />                bola_tt = 10<br />                bol.top = 10<br />          <br />        <br />    if tecla_pres&#91;K_DOWN&#93;:<br />        bol.top = bol.top + 5<br />        bola_tt = bola_tt + 5<br />        <br />        <br />        if bola_tt &gt; 210:<br />            if bol.top  &lt;4970&gt; 210:<br />                   <br />                   bola_tt = 210<br />                   campo_y = campo_y - ayu<br />            else:      <br />                bola_tt = 210<br />                bol.top = 4970<br />             <br />             <br />              <br />              <br />              <br />    for event in pygame.event.get&#40;&#41;:<br />        if event.type == QUIT:<br />            n = 1<br />        if event.type == KEYDOWN:<br />            if event.key == K_ESCAPE:<br /