lt; endl&lt;&lt;&quot;OK&quot;&lt;&lt;endl;<br />      return 0;<br />&#125;<br />void compatibilidad_video_sdl&#40;int w, int h, int bpp&#41;<br />&#123;<br />   SDL_Surface *pantalla;<br />//SWSURFACE    superficie en la memoria principal &#40;por software&#41;<br />   pantalla = SDL_SetVideoMode&#40;w, h, bpp, SDL_SWSURFACE&#41;;<br />   if &#40;pantalla == NULL&#41;<br />   &#123;<br />      cout &lt;&lt;&quot; SDL_SWSURFACE hwbpp: &quot;&lt;&lt;h&lt;&lt;&quot; &quot;&lt;&lt;w&lt;&lt;&quot; &quot;&lt;&lt;bpp&lt;&lt;&quot; No compatible, El error es: &quot;&lt;&lt;SDL_GetError&#40;&#41;&lt;&lt;endl;<br />   &#125;<br />   else<br />   &#123;<br />      cout &lt;&lt;&quot; SDL_SWSURFACE hwbpp: &quot;&lt;&lt;h&lt;&lt;&quot; &quot;&lt;&lt;w&lt;&lt;&quot; &quot;&lt;&lt;bpp&lt;&lt;&quot; Es compatible&quot;&lt;&lt;endl;<br />   &#125;<br />   <br />   <br />//HWSURFACE    superficie en la memoria de la gpu &#40;hardware&#41;<br />   pantalla = SDL_SetVideoMode&#40;w, h, bpp, SDL_HWSURFACE&#41;;<br />   if &#40;pantalla == NULL&#41;<br />   &#123;<br />      cout &lt;&lt;&quot; SDL_HWSURFACE hwbpp: &quot;&lt;&lt;h&lt;&lt;&quot; &quot;&lt;&lt;w&lt;&lt;&quot; &quot;&lt;&lt;bpp&lt;&lt;&quot; No compatible, El error es: &quot;&lt;&lt;SDL_GetError&#40;&#41;&lt;&lt;endl;<br />   &#125;<br />   else<br />   &#123;<br />      cout &lt;&lt;&quot; SDL_HWSURFACE hwbpp: &quot;&lt;&lt;h&lt;&lt;&quot; &quot;&lt;&lt;w&lt;&lt;&quot; &quot;&lt;&lt;bpp&lt;&lt;&quot; Es compatible&quot;&lt;&lt;endl;<br />   &#125;<br /><br />   <br /> //HWSURFACE+DOUBLEBUFFER<br />   pantalla = SDL_SetVideoMode&#40;w, h, bpp, SDL_HWSURFACE | SDL_DOUBLEBUF&#41;;<br />   if &#40;pantalla == NULL&#41;<br />   &#123;<br />      cout &lt;&lt;&quot; SDL_HWSURFACE con SDL_DOUBLEBUF hwbpp: &quot;&lt;&lt;h&lt;&lt;&quot; &quot;&lt;&lt;w&lt;&lt;&quot; &quot;&lt;&lt;bpp&lt;&lt;&quot; No compatible, El error es: &quot;&lt;&lt;SDL_GetError&#40;&#41;&lt;&lt;endl;<br />   &#125;<br />   else<br />   &#123;<br />      co